mapper.select:
SELECT * FROM shopping_cart 
WHERE user_id = #{userId}
ORDER BY created_at DESC

mapper.update:
UPDATE shopping_cart 
SET quantity = #{quantity}, updated_at = NOW()
WHERE user_id = #{userId} AND product_id = #{productId}

mapper.delete:
DELETE FROM shopping_cart 
WHERE user_id = #{userId} 

    AND product_id IN

        #{productId}
